    #[allow(clippy::clone_on_copy)]
    #[allow(clippy::vec_init_then_push)]
    pub fn invoke_signed(&self, signers_seeds: &[&[&[u8]]]) -> solana_program_error::ProgramResult {
        let args = AddCrewToGameInstructionArgs {
            items: self.instruction.items.clone().expect("items is not set"),
        };
        let instruction = AddCrewToGameCpi {
            __program: self.instruction.__program,

            sage_player_profile: self
                .instruction
                .sage_player_profile
                .expect("sage_player_profile is not set"),

            starbase: self.instruction.starbase.expect("starbase is not set"),

            starbase_player: self
                .instruction
                .starbase_player
                .expect("starbase_player is not set"),

            sage_crew_config: self
                .instruction
                .sage_crew_config
                .expect("sage_crew_config is not set"),

            key: self.instruction.key.expect("key is not set"),

            profile: self.instruction.profile.expect("profile is not set"),

            profile_faction: self
                .instruction
                .profile_faction
                .expect("profile_faction is not set"),

            game_id: self.instruction.game_id.expect("game_id is not set"),

            crew_config: self
                .instruction
                .crew_config
                .expect("crew_config is not set"),

            crew_owner: self.instruction.crew_owner.expect("crew_owner is not set"),

            crew_delegate: self
                .instruction
                .crew_delegate
                .expect("crew_delegate is not set"),

            log_wrapper: self
                .instruction
                .log_wrapper
                .expect("log_wrapper is not set"),

            compression_program: self
                .instruction
                .compression_program
                .expect("compression_program is not set"),

            bubblegum_program: self
                .instruction
                .bubblegum_program
                .expect("bubblegum_program is not set"),

            system_program: self
                .instruction
                .system_program
                .expect("system_program is not set"),
            __args: args,
        };
        instruction.invoke_signed_with_remaining_accounts(
            signers_seeds,
            &self.instruction.__remaining_accounts,
        )
    }
